import React, { useEffect, useState } from 'react';
import { PostsService } from '../services/posts.service';
import PostsTable from '../components/postsTable';
import { useParams } from 'react-router-dom';
import { Post } from '../models/posts.interfaces';
import { observer } from 'mobx-react';
import { PostsStore } from '../store/userPosts';

const PostsUser: React.FC = observer(() => {
  const { userId } = useParams();
  const [posts, setPosts] = useState<Post[]>([postsStore.posts]);
  const postsStore = new PostsStore();
  const [deletePost, setDeletePost] = useState<Post | null>(null);

  useEffect(() => {
    const fetchPostsByUser = async () => {
      const postsData: Post[] = await PostsService.getUserPosts(Number(userId));
      postsStore.setPosts(postsData);
      setPosts(postsStore.posts);
    };
    fetchPostsByUser();
  }, [userId]);

  useEffect(() => {
    const deletePostById = async () => {
      if (deletePost) {
        await PostsService.deletePostById(deletePost.id);
        postsStore.setPosts(posts.filter((post) => post.id !== deletePost.id));
        setPosts(postsStore.posts);
      }
    };
    deletePostById();
  }, [deletePost]);

  return (
    <div>
      <PostsTable posts={posts} setDeletePost={setDeletePost} />
    </div>
  );
});

export default PostsUser;
