import React, { useEffect, useState } from 'react';
import { PostsService } from '../services/posts.service';
import PostsTable from '../components/postsTable';
import { useParams } from 'react-router-dom';
import { Post } from '../models/posts.interfaces';
import { io, Socket } from 'socket.io-client';
import '../assets/css/posts.css';

const PostsUser: React.FC = () => {
  const { userId } = useParams();
  const [posts, setPosts] = useState<Post[]>([]);
  const [socket, setSocket] = useState<Socket | null>(null);

  useEffect(() => {
    const socket = io('http://localhost:8001');
    setSocket(socket);
    socket.on('postDeleted', handlePostDeleted);
    socket.on('postCreated')
    return () => {
      if (socket) {
        socket.disconnect();
      }
    };
  }, []);

  useEffect(() => {
    const fetchPostsByUser = async () => {
      setPosts(await PostsService.getUserPosts(Number(userId)));
    };
    fetchPostsByUser();
  }, [userId]);

  const handleDeletePost = async (postId: number) => {
    await PostsService.deletePostById(postId);
    socket?.emit('postDeleted', postId);
  };

  const handlePostDeleted = (postId: number) => {
    setPosts((prevPosts) =>
      prevPosts.filter((post) => post.id !== Number(postId)),
    );
  };

  return (
    <div>
      <PostsTable posts={posts} onDeletePost={handleDeletePost} />
    </div>
  );
};

export default PostsUser;
