import React, { useEffect, useState } from 'react';
import { PostsService } from '../services/posts.service';
import PostsTable from '../components/postsTable';
import { useParams } from 'react-router-dom';
import { Post } from '../models/posts.interfaces';
import { io, Socket } from 'socket.io-client';
import '../assets/css/posts.css';

const PostsUser: React.FC = () => {
  const { userId } = useParams();
  const [posts, setPosts] = useState<Post[]>([]);
  const [deletePost, setDeletePost] = useState<Post | null>(null);
  const [socket, setSocket] = useState<Socket>();

  useEffect(() => {
    const fetchPostsByUser = async () => {
      setPosts(await PostsService.getUserPosts(Number(userId)));
    };
    fetchPostsByUser();
  }, [userId]);

  useEffect(() => {
    setSocket(io('http://localhost:3000'));
  }, []);

  const send = (postId: number) => {
    socket?.emit('postDeleted', postId);
  };

  useEffect(() => {
    const deletePostById = async () => {
      if (deletePost) {
        await PostsService.deletePostById(deletePost.id);
        send(deletePost.id);
      }
    };
    deletePostById();
  }, [deletePost]);

  useEffect(() => {
    const updatePosts = async () => {
      if (socket) {
        socket.on('postDeleted', (postId: number) => {
          setPosts(posts.filter((post) => post.id !== postId));
        });
      }
    };
    updatePosts();
  });

  return (
    <div>
      <PostsTable posts={posts} setDeletePost={setDeletePost} />
    </div>
  );
};

export default PostsUser;
